let tmp=context.get("updateData");
if (tmp==undefined){
    tmp={};
}
switch (msg.topic){
    case "updateMid":
        tmp.mid=msg.payload;
        context.set("updateData", tmp);
        msg=null;
        msg2=null;
        break;
    case "imsi":
        tmp.imsi = msg.payload;
        context.set("updateData", tmp);
        msg = null;
        msg2 = null;
        break;
    case "sn":
        tmp.sn = msg.payload;
        context.set("updateData", tmp);
        msg = null;
        msg2 = null;
        break;
    case "change":
        if (tmp.mid){
            let data;
            if (tmp.imsi){
                //data.push(0x02);
                //data.push(tmp.mid);
                const buf0 = Buffer.from([0x02,tmp.mid]);
                const buf1 = Buffer.from(tmp.imsi);
                const buf2 = Buffer.from('\0');
                //data=data.concat(Buffer.from(tmp.imsi).values());
                if (((buf0.length+buf1.length)%2)>0){
                    data = Buffer.concat([buf0,buf1,buf2]);
                }else{
                    data = Buffer.concat([buf0, buf1]);
                }
                const dLength=data.length;///2;
                msg.payload = {
                    value: data,//msg.payload,
                    fc: 16,
                    unitid: 0,
                    address: 0xc830,//0,
                    quantity: dLength
                };
                flow.set("ntn_unitid", tmp.mid);

                msg2.updateModbusID = tmp.mid;
                context.set("updateData", undefined);
            }else if(tmp.sn){
                const buf0 = Buffer.from([0x01, tmp.mid]);
                const buf1 = Buffer.from(tmp.sn);
                const buf2 = Buffer.from('\0');
                if (((buf0.length + buf1.length) % 2) > 0) {
                    data = Buffer.concat([buf0, buf1, buf2]);
                } else {
                    data = Buffer.concat([buf0, buf1]);
                }
                const dLength = data.length;// / 2;
                msg.payload = {
                    value: data,//msg.payload,
                    fc: 16,
                    unitid: 0,
                    address: 0xc830,//0,
                    quantity: dLength
                };
                flow.set("ntn_unitid", tmp.mid);
                msg2.updateModbusID=tmp.mid;

                context.set("updateData", undefined);
            }else{
                msg2.payload = "Need IMSI or Serial Number information";
                msg = null;
                msg2 = null;
            }

        }else{
            msg2.payload="What's new modbus ID?";
            msg=null;
            msg2 = null;
        }
        //msg.payload=tmp;
        break;

}
